'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var code = 'var x = 123n;';

linter.defineRule( 'no-builtin-big-int', rule );

var results = linter.verify( code, {
	'rules': {
		'no-builtin-big-int': 'error'
	},
	'parserOptions': {
		'ecmaVersion': 2020
	}
});
console.log( results );

