'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var x = 123n;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the built-in global `BigInt` literal syntax is not allowed; use `@stdlib/bigint/ctor` instead.',
			'type': 'Literal'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var x = { a: 123n }'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the built-in global `BigInt` literal syntax is not allowed; use `@stdlib/bigint/ctor` instead.',
			'type': 'Literal'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var x = { a: BigInt( 123 ) }'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the built-in global `BigInt` constructor is not allowed; use `@stdlib/bigint/ctor` instead.',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var x = BigInt( 123 );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the built-in global `BigInt` constructor is not allowed; use `@stdlib/bigint/ctor` instead.',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function createBigInt() {',
		'  return BigInt( 123 );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the built-in global `BigInt` constructor is not allowed; use `@stdlib/bigint/ctor` instead.',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
