'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var arr = new Array();'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the `new Array()` constructor is not allowed; use an array literal with push instead.',
			'type': 'NewExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var arr = new Array(10);'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the `new Array()` constructor is not allowed; use an array literal with push instead.',
			'type': 'NewExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var arr = new Array(1, 2, 3, 4);'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the `new Array()` constructor is not allowed; use an array literal with push instead.',
			'type': 'NewExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function createArray() {',
		'  return new Array(5);',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Using the `new Array()` constructor is not allowed; use an array literal with push instead.',
			'type': 'NewExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
