'use strict';

// MODULES //

var logger = require( 'debug' );
var readJSON = require( '@stdlib/fs/read-json' );
var validateName = require( './validate_name.js' );


// VARIABLES //

var debug = logger( 'lint:pkg-json-names:linter' );


// MAIN //

/**
* Reads and lints package.json files for name validation.
*
* @private
* @param {StringArray} files - list of package.json files
* @param {Callback} clbk - callback to invoke upon completion
* @returns {void}
*/
function lint( files, clbk ) {
	var total;
	var out;
	var i;

	total = files.length;
	out = [];
	i = -1;

	return next();

	/**
	* Processes the next package.json file.
	*
	* @private
	* @returns {void}
	*/
	function next() {
		i += 1;
		if ( i >= total ) {
			debug( 'Processed all files.' );
			return done();
		}
		debug( 'Processing file %d of %d: %s', i+1, total, files[ i ] );
		readJSON( files[ i ], onRead );
	}

	/**
	* Callback invoked upon reading a package.json file.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {Object} json - JSON object
	* @returns {void}
	*/
	function onRead( error, json ) {
		var err;
		var j;

		j = i + 1;
		if ( error ) {
			debug( 'Encountered an error reading file: %s (%d of %d). Error: %s', files[ i ], j, total, error.message );
			out.push({
				'file': files[ i ],
				'error': {
					'message': 'Failed to parse package.json: ' + error.message
				}
			});
		} else {
			debug( 'Successfully read file: %s (%d of %d).', files[ i ], j, total );

			debug( 'Validating package name.' );
			err = validateName( files[ i ], json );
			if ( err ) {
				debug( 'Package name validation failed: %s.', JSON.stringify( err ) );
				out.push({
					'file': files[ i ],
					'error': err
				});
			} else {
				debug( 'Package name is valid.' );
			}
		}
		if ( j < total ) {
			debug( 'Processed %d of %d files.', j, total );
			return next();
		}
		debug( 'Processed all files.' );
		return done();
	}

	/**
	* Callback invoked upon completion.
	*
	* @private
	* @returns {void}
	*/
	function done() {
		if ( out.length ) {
			return clbk( null, out );
		}
		return clbk( null, null );
	}
}


// EXPORTS //

module.exports = lint;
